<?php    use Controller\IN_Controller;    class Coupon extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else {				$permissionArray = Session::get('aPermissionArray');				Functions::checkPermission($permissionArray,Functions::getUrl()[1]);				Functions::setOn($_SESSION['adId']);			}        }        public function create()        {            $this->view->render('coupon/create');        }        public function createcoupon()        {            $this->model->createCoupon();        }        public function used()        {            $this->view->usedCoupon = $this->model->usedcoupon();            $this->view->render('coupon/used');        }        public function unused()        {            $this->view->usedCoupon = $this->model->unusedcoupon();            $this->view->render('coupon/unused');        }        public function delete($arg = null)        {            $this->model->delete($arg,1);        }        public function deleten($arg = null){            $this->model->delete($arg,2);        }    }