<?php    use Controller\IN_Controller;    class Event extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else {				$permissionArray = Session::get('aPermissionArray');				Functions::checkPermission($permissionArray,Functions::getUrl()[1]);				Functions::setOn($_SESSION['adId']);			}        }        public function create()        {        	$this->view->all = $this->model->create();            $this->view->render('event/create');			if ($_POST)            	$this->model->openevent();        }        public function created(){        	$this->helper(['filter']);        	if ($_POST)            	$this->model->created();        }        public function plan()        {            $this->view->plan = $this->model->plan();            $this->view->render('event/plan');        }        public function plancreate()        {            $this->model->plancreate();        }		public function plancreate2()		{			$this->model->plancreate2();		}        public function liste()        {            $this->view->event = $this->model->liste();            $this->view->render('event/list');        }        public function delete($arg = null)        {            if ($arg == null)            {                $this->view->render('error/index',true);                die;            }            $this->model->delete($arg);        }		public function delete2($arg = null)		{			if ($arg == null)			{				$this->view->render('error/index',true);				die;			}			$this->model->delete2($arg);		}        public function edit($arg = null)        {            if ($arg == null)            {                $this->view->render('error/index',true);                die;            }            $this->view->event = $this->model->edit($arg);            $this->view->render('event/edit');        }        public function change()        {            if ($_POST)                $this->model->change();            else                $this->view->render('error/index',true);        }        public function close($arg = null)		{			$this->helper(['filter']);			if ($arg === null)				return false;			else			{				$this->model->close($arg);			}		}    }