<?phpuse Controller\IN_Controller;    class Index extends IN_Controller    {        public function __construct()        {            parent::__construct();            Session::init();            $permissionArray = isset($_SESSION['aPermissionArray']) ? $_SESSION['aPermissionArray'] : null;            if($permissionArray == null){                URI::redirect('login');                die;            }            Functions::checkPermission($permissionArray,Functions::getUrl()[1]);            Functions::setOn($_SESSION['adId']);        }        public function index()        {            $this->view->dashboard = $this->model->dashboard();            $this->view->render('index/index');        }    }