<?php    use Controller\IN_Controller;    class Log extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else			{				$permissionArray = Session::get('aPermissionArray');				Functions::checkPermission($permissionArray,Functions::getUrl()[1]);				Functions::setOn($_SESSION['adId']);			}        }        public function shop()        {            $this->view->shop = $this->model->shop();            $this->view->render('log/shop');        }        public function active(){            $this->model->active();        }        public function ban()        {            $this->view->ban = $this->model->ban();            $this->view->render('log/ban');        }		public function hwidban()        {            $this->view->hwidban = $this->model->hwidban();            $this->view->render('log/hwidban');        }		public function guvenlipc()        {            $this->view->guvenlipc = $this->model->guvenlipc();            $this->view->render('log/guvenlipc');        }		public function guvenlipc2()        {            $this->view->guvenlipc2 = $this->model->guvenlipc2();            $this->view->render('log/guvenlipc');        }		public function virustotal()        {            $this->view->virustotal = $this->model->virustotal();            $this->view->render('log/virustotal');        }		public function oxoyun()        {            $this->view->oxoyun = $this->model->oxoyun();            $this->view->render('log/oxoyun');        }		public function kasagame()        {            $this->view->kasagame = $this->model->kasagame();            $this->view->render('log/kasagame');        }		public function paywant()        {            $this->view->paywant = $this->model->paywant();            $this->view->render('log/paywant');        }		public function payreks()        {            $this->view->payreks = $this->model->payreks();            $this->view->render('log/payreks');        }        public function changeempire(){            $this->model->changeempire();        }        public function getitem(){            $this->model->getitem();        }        public function deletecache(){            $this->model->deletecache();        }		public function deletecloudflarecache(){            $this->model->deletecloudflarecache();        }    }