<?php    use Controller\IN_Controller;    class Login extends IN_Controller {        public function __construct()        {            parent::__construct();            Session::init();            unset($_SESSION['aLogin']);            unset($_SESSION['aPermission']);            unset($_SESSION['aPermissionArray']);            unset($_SESSION['allPermissionArray']);            unset($_SESSION['aName']);            unset($_SESSION['adId']);        }        public function index(){            $this->view->render('login/index',1);        }        public function check(){            if($_POST){                $this->helper(['hash']);                $this->model->check();            }        }        public function createkey(){            if($_POST){                $this->helper(['hash']);                $this->model->createkey();            }        }    }