<?php    use Controller\IN_Controller;    class Player extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");        }        public function search()        {            if ($_POST){                $this->helper(['filter']);                $this->view->search = $this->model->search();                $this->view->render('player/search');            }else{                $this->view->render('error/index',1);            }        }		public function shopsearch()        {            if ($_POST){                $this->helper(['filter']);                $this->view->shopsearch = $this->model->shopsearch();                $this->view->render('player/shopsearch');            }else{                $this->view->render('error/index',1);            }        }        public function player($arg)        {            if ($arg == null){                $this->view->render('error/index',1);            }else{                $this->helper(['filter']);                $this->view->info = $this->model->player($arg);                $this->view->render('player/player');            }        }        public function deleteitem1($arg = null){			if ($arg == null){                $this->view->render('error/index',1);            }else{                $this->helper(['filter']);                $this->model->deleteitem1($arg);            }        }		public function shopclose($arg = null){			if ($arg == null){                $this->view->render('error/index',1);            }else{                $this->helper(['filter']);                $this->model->shopclose($arg);            }        }    }