<?php 	use Controller\IN_Controller;    class Product extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else {				$permissionArray = Session::get('aPermissionArray');				Functions::checkPermission($permissionArray,Functions::getUrl()[1]);				Functions::setOn($_SESSION['adId']);			}        }        public function view()        {            $this->view->view = $this->model->view();            $this->view->render('product/view');        }        public function getview()        {            $this->model->getview();        }        public function search()        {            $this->view->search = $this->model->search();            $this->view->render('product/search');        }        public function item($arg = null)        {            if($arg != null){                $this->view->item = $this->model->item($arg);                $this->view->render('product/item');            }        }        public function imageupload($arg = null){            if($_FILES){                $this->helper(['upload']);                $this->model->imageupload($arg);            }        }		public function wheel_imageupload($arg = null){			if($_FILES){				$this->helper(['upload']);				$this->model->wheel_imageupload($arg);			}		}        public function itemadd($arg)        {            if ($_POST){                $this->model->itemadd($arg);            }        }        public function views()        {            $this->view->items = $this->model->views();            $this->view->render('product/views');        }        public function itemdelete($arg)        {           $this->model->itemdelete($arg);        }        public function item_active($arg)		{			$this->model->item_active($arg);		}        public function discount($arg = null)        {            $this->view->itemId = $this->model->discount($arg);            $this->view->render('product/discount');//            $this->model->discountOk($arg);        }        public function discountok($arg = null)        {            $this->model->discountok($arg);        }        public function discountno($arg = null)        {            $this->model->discountno($arg);        }        public function popularok($arg)		{			$this->model->popularok($arg);		}		public function popularno($arg)		{			$this->model->popularno($arg);		}        public function edit($arg)        {            if($arg != null){                $this->view->getItem = $this->model->edit($arg);                $this->view->render('product/edit');            }        }        public function change($arg = null)        {            if($arg != null){                $this->model->change($arg);            }        }        public function categoryadd()        {            $this->view->render('product/categoryadd');        }        public function categoryaddok(){            if($_POST){                $this->model->categoryaddok();            }        }        public function categorylist()        {            $this->view->categorylist = $this->model->categorylist();            $this->view->render('product/categorylist');        }        public function categorydelete($arg = null)        {            $this->model->categorydelete($arg);        }		public function bonusadd(){            $this->view->render('product/bonusadd');			if($_POST){                $this->model->bonusaddok();            }        }        public function bonuslist()        {            $this->view->bonuslist = $this->model->bonuslist();            $this->view->render('product/bonuslist');        }        public function bonusdelete($arg = null)        {            $this->model->bonusdelete($arg);        }        public function deleteall()		{			$this->model->deleteall();		}		public function wheel()		{			$this->view->view = $this->model->view();			$this->view->render('product/wheel');		}		public function wheel_item($arg){			if($arg != null){				$this->view->item = $this->model->item($arg);				$this->view->render('product/wheel_item');			}		}		public function wheel_itemadd($arg)		{			if ($_POST){				$this->model->wheel_itemadd($arg);			}		}		public function wheel_liste()		{			$this->view->items = $this->model->wheel_liste();			$this->view->render('product/wheel_liste');		}		public function wheel_itemdelete($arg)		{			$this->model->wheel_itemdelete($arg);		}		public function wheel_deleteall()		{			$this->model->wheel_deleteall();		}	}