<?php    use Controller\IN_Controller;    class Profile extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");        }        public function index()        {            $this->view->profile = $this->model->index();            $this->view->render('profile/index');        }        public function password()        {            if ($_POST)            {                $this->helper(['filter','hash']);                $this->model->password();            }else{                $this->view->render('errors/index');            }        }        public function change()        {            if ($_POST)            {                $this->helper(['filter']);                $this->model->change();            }else{                $this->view->render('errors/index');            }        }        public function imageupload($arg = null){			if($_FILES){				$this->helper(['upload']);                $this->model->imageupload($arg);            }        }    }