<?php    use Controller\IN_Controller;    class Socket extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else			{				$permissionArray = Session::get('aPermissionArray');				Functions::checkPermission($permissionArray,Functions::getUrl()[1]);				Functions::setOn($_SESSION['adId']);			}        }        public function dc()        {            $this->view->render('socket/dc');        }        public function dcset()        {            if ($_POST){                $this->helper(['filter']);                $this->model->dcset();            }else{                $this->view->render('error/index');            }        }        public function chat(){            $this->view->render('socket/chat');        }        public function chatset()        {            if($_POST){                $this->helper(['filter']);                $this->model->chatset();            }else{                $this->view->render('error/index');            }        }        public function notice()        {            $this->view->render('socket/notice');        }        public function noticeset()        {            if($_POST){                $this->model->noticeset();            }else{                $this->view->render('error/index');            }        }		public function play()        {            $this->view->render('socket/play');        }        public function playset()        {            if($_POST){                $this->model->playset();            }else{                $this->view->render('error/index');            }        }        public function drop()		{			$this->view->render('socket/drop');		}		public function drop_open()		{			$this->helper(['filter']);			if($_POST)				$this->model->drop_open();			else				$this->view->render('error/index');		}    }