<?php/** * Created by PhpStorm. * User: user * Date: 4.02.2017 * Time: 20:50 */use Controller\IN_Controller;class Sql extends IN_Controller{	public function __construct()	{		parent::__construct();		if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))			die("Permission Denied!");		else {		Session::init();		$permissionArray = Session::get('aPermissionArray');		Functions::checkPermission($permissionArray,Functions::getUrl()[1]);		Functions::setOn($_SESSION['adId']);		}	}	public function gm()	{		$this->view->render('sql/gm');	}	public function proto()	{		$this->view->render('sql/proto');	}	public function attr()	{		$this->view->render('sql/attr');	}	public function all()	{		$this->view->render('sql/all');	}	public function clear()	{		$this->view->render('sql/clear');	}	public function offprice()	{		$this->view->all = $this->model->offprice();		$this->view->render('sql/offprice');	}	public function offpriceadd()	{		if ($_POST)			$this->model->offpriceadd();	}	public function offpricedelete($arg)	{		$this->model->offpricedelete($arg);	}	public function npc()	{		$this->view->npcliste = $this->model->npcliste();		$this->view->render('sql/npc');	}	public function npcitemekle()	{		$this->view->npcliste = $this->model->npcliste();		$this->view->npcitemlist = $this->model->item_proto();		$this->view->render('sql/npcitemekle');		if ($_POST)			$this->model->npcitemekle();	}	public function npcitemduzenle()	{		$this->view->npcliste = $this->model->npcliste();		$this->view->render('sql/npcitemduzenle');		if ($_POST)			$this->model->npcitemekle();	}	public function npcitemduzenle2($arg = null)	{		if ($arg == null){			$this->view->render('error/index',1);		}else{			$this->helper(['filter']);			$this->view->info = $this->model->npcitemduzenle2($arg);			$this->view->render('sql/npcitemduzenle2');		}	}	public function npcitemkaldir($arg1,$arg2)	{		$this->model->npcitemkaldir($arg1,$arg2);	}	public function npctur($arg = null)	{		if ($arg == null){			$this->view->render('error/index',1);		}else{			$this->helper(['filter']);			$this->view->info = $this->model->npctur($arg);			$this->view->render('sql/npctur');			if ($_POST)				$this->model->npcturdegistir();		}	}	public function npcekle()	{		$this->view->npcliste = $this->model->npc_list();		$this->view->render('sql/npcekle');		if ($_POST)				$this->model->npcekle();	}}