<?php    use Controller\IN_Controller;    class User extends IN_Controller    {        public function __construct()        {            parent::__construct();			if (!isset($_SESSION['aLogin']) || !isset($_SESSION['adId']))				die("Permission Denied!");			else {				$permissionArray = Session::get('aPermissionArray');            	Functions::checkPermission($permissionArray,Functions::getUrl()[1]);            	Functions::setOn($_SESSION['adId']);			}        }        public function index()        {            $this->view->create = $this->model->index();            $this->view->render('user/index');        }        public function create()        {            if ($_POST){                $this->helper(['hash']);                $this->model->create();            }else{                $this->view->render('error/index',1);            }        }        public function users()        {            $this->view->users = $this->model->users();            $this->view->render('user/users');        }        public function delete($arg=null)        {            if ($arg == null){                $this->view->render('error/index',1);            }else{                $this->helper(['filter']);                $this->model->delete($arg);            }        }        public function edit($arg)        {            if ($arg == null)                $this->view->render('error/index',1);            else            {                $this->view->user = $this->model->edit($arg);                $this->view->render('user/edit');            }        }    }