<script src="<?=URI::public_path();?>pages/scripts/jquery.lazyload.js" type="text/javascript"></script><script src="https://cdn.jsdelivr.net/npm/select2@4.0.12/dist/js/select2.min.js"></script><script src="<?=URI::public_path();?>pages/scripts/ui-modals.min.js" type="text/javascript"></script><script>    $("#itemList").select2({dropdownAutoWidth : true});    $('#itemList').on('select2:select', function (e) {        var data = e.params.data;        var url = $(this).data("url") + data.id;        $('#itemListHref').attr('href',url);    });</script>