<?php if (Functions::checkAllPermission($_SESSION['allPermissionArray'],"18") == true):?><?php$oyunAdi = \StaticDatabase\StaticDatabase::settings('oyun_adi');$slogan = \StaticDatabase\StaticDatabase::settings('slogan');$cache = \StaticDatabase\StaticDatabase::settings('cachetime');$captchaStatus = ['0'=>'Pasif','1'=>'Aktif'];?><div class="row">    <div class="col-md-12 ">        <!-- BEGIN SAMPLE FORM PORTLET-->        <div class="portlet light bordered">            <div class="portlet-title">                <div class="caption font-red-sunglo">                    <i class="icon-settings font-red-sunglo"></i>                    <span class="caption-subject bold uppercase"> Site Ayarları</span>                </div>            </div>            <div class="portlet-body form">                <form role="form" id="siteSetting" action="<?=URI::get_path('setting/siteedit')?>" method="post" >                    <div class="form-body">                        <div class="form-group form-md-line-input form-md-floating-label">                            <input type="text" name="title" class="form-control" id="form_control_1" value="<?=$oyunAdi?>">                            <label for="form_control_1">Site Başlığı</label>                            <span class="help-block">Site Başlığı Belirleyiniz...</span>                        </div>                        <div class="form-group form-md-line-input form-md-floating-label">                            <input type="text" name="slogan" class="form-control" id="form_control_1" value="<?=$slogan?>">                            <label for="form_control_1">Slogan</label>                            <span class="help-block">Oyun Sloganını Belirleyiniz...</span>                        </div>						<div class="form-group form-md-line-input form-md-floating-label">                            <input type="text" name="cachetime" class="form-control" id="form_control_1" value="<?=$cache?>">                            <label for="form_control_1">Cache Süresi</label>                            <span class="help-block">Saniye cinsinde yazınız! Örn: 30 DK > 1800</span>                        </div>                    </div>                    <div class="form-actions noborder">                        <button type="submit" id="settingButton" class="btn blue">Kaydet</button>                    </div>                </form>            </div>        </div>    </div>    <div class="col-md-12 ">        <!-- BEGIN SAMPLE FORM PORTLET-->        <div class="portlet light form-fit bordered">            <div class="portlet light bordered">                <div class="portlet-title">                    <div class="caption font-red-sunglo">                        <i class="fa fa-language font-red-sunglo"></i>                        <span class="caption-subject bold uppercase"> Dil Ayarları </span>                    </div>                </div>                <div class="portlet-body form">                    <form id="paywantedit" role="form" action="<?=URI::get_path('setting/langedit');?>" method="post">                        <div class="form-body">                            <div class="form-group form-md-line-input form-md-floating-label">                                <select class="form-control edited" name="status">									<?php foreach ($captchaStatus as $key=>$row):?>										<?php if ($key == \StaticDatabase\StaticDatabase::settings('multi_languages')):?>                                            <option value="<?=$key?>" selected><?=$row?></option>										<?php else:?>                                            <option value="<?=$key?>"><?=$row?></option>										<?php endif;?>									<?php endforeach;?>                                </select>                                <label for="form_control_1">Varsayılan Dil </label>                            </div>                            <div class="form-group form-md-line-input form-md-floating-label">                                <select class="form-control edited" name="lang">									<?php foreach ($this->site as $row):?>										<?php if ($row['code'] == \StaticDatabase\StaticDatabase::settings('default_language')):?>                                            <option value="<?=$row['code']?>" selected><?=$row['name']?></option>										<?php else:?>                                            <option value="<?=$row['code']?>"><?=$row['name']?></option>										<?php endif;?>									<?php endforeach;?>                                </select>                                <label for="form_control_1">Varsayılan Dil </label>                            </div>                        </div>                        <div class="form-actions noborder">                            <button type="submit" class="btn green mt-ladda-btn ladda-button" data-style="contract">                                <span class="ladda-label">Güncelle</span>                            </button>                        </div>                    </form>                </div>            </div>        </div>    </div>	<div class="col-md-12 ">        <!-- BEGIN SAMPLE FORM PORTLET-->        <div class="portlet light bordered">            <div class="portlet-title">                <div class="caption font-red-sunglo">                    <i class="icon-settings font-red-sunglo"></i>                    <span class="caption-subject bold uppercase"> Footer Ayarları</span>                </div>            </div>            <div class="portlet-body form">                <form role="form" id="footerSetting" action="<?=URI::get_path('setting/footeredit')?>" method="post" >                    <div class="form-body">                        <div class="form-group form-md-line-input form-md-floating-label">                            <input type="text" name="name" class="form-control" id="form_control_1" value="<?=\StaticDatabase\StaticDatabase::settings('footer_name')?>">                            <label for="form_control_1">Footer Başlığı</label>                            <span class="help-block">Footer Başlığı Belirleyiniz...</span>                        </div>                        <div class="form-group form-md-line-input form-md-floating-label">                            <input type="text" name="link" class="form-control" id="form_control_1" value="<?=\StaticDatabase\StaticDatabase::settings('footer_link')?>">                            <label for="form_control_1">Footer Linki</label>                            <span class="help-block">Footer Linki Belirleyiniz...</span>                        </div>                    </div>                    <div class="form-actions noborder">                        <button type="submit" id="settingButton" class="btn blue">Kaydet</button>                    </div>                </form>            </div>        </div>    </div></div><script>    $('#siteSetting').submit(function (event) {       event.preventDefault();       var url = $(this).attr('action');       var data = $(this).serialize();        $.ajax({            url: url,            dataType: 'json',            type: 'post',            data: data,            processData: false,            success: function(result){                if (result.result == false){                    notify('error','Herhangi bir değişiklik yapılmadı');                }else if(result.result == true){                    notify('success','Bilgiler güncellendi.');                }            },            error: function( jqXhr, textStatus, errorThrown ){                console.log( errorThrown );            }        });    });    $('#logoSetting').submit(function (event) {        event.preventDefault();        var url = $(this).attr('action');        var data = new FormData($(this)[0]);        $.ajax({            url: url,            dataType: 'json',            type: 'post',            data: data,            cache:false,            contentType: false,            processData: false,            success: function(result){                if (result.result == false){                    notify('error','Lütfen önce resim seçiniz');                }else if(result.result == true){                    notify('success','Logo başarıyla değiştirildi');                    var base_url = window.location.origin;                    var pathArray = window.location.pathname.split( '/' );                    $('#myLogo').attr('src',base_url+'/'+pathArray[1]+'/'+result.data[0]['paths']);                }            },            error: function( jqXhr, textStatus, errorThrown ){                console.log( errorThrown );            }        });    });    $('#paywantedit').submit(function (eve) {        eve.preventDefault();        var url = $(this).attr('action');        var data = new FormData($(this)[0]);        $.ajax({            type: "POST",            url: url,            data: data,            dataType: "json",            cache: false,            contentType: false,            processData: false,            success: function (result) {                if (result.result === true)                    notify('success','Güncelleme Başarılı');                else                    notify('error','Bir Hata Oluştu');            }        });    });    $('#footerSetting').submit(function (event) {        event.preventDefault();        var url = $(this).attr('action');        var data = $(this).serialize();        $.ajax({            url: url,            dataType: 'json',            type: 'post',            data: data,            processData: false,            success: function(result){                if (result.result == false){                    notify('error','Herhangi bir değişiklik yapılmadı');                }else if(result.result == true){                    notify('success','Bilgiler güncellendi.');                }            },            error: function( jqXhr, textStatus, errorThrown ){                console.log( errorThrown );            }        });    });</script><?php endif;?>